// Importowanie potrzebnych zależności z biblioteki React
import React, { useState } from 'react';
import './App.css';  // Importowanie pliku CSS

// ... (reszta importów)

function ContactForm() {
  const [formData, setFormData] = useState({
      name: '',
      phone: '',
      email: '',
      message: ''
  });
  const [error, setError] = useState(null);

  const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({
          ...formData,
          [name]: value
      });
  };

  const handleSubmit = async (e) => {
      e.preventDefault();
      try {
          const response = await fetch('http://localhost:3001/contact', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify(formData),
          });
          const data = await response.json();
          if (!response.ok) {
              throw new Error(data.message);
          }
          console.log('Dane zapisane pomyślnie');
          setError(null);
      } catch (error) {
          setError(error.message);
          console.log('Error:', error);
      }
  };

  return (
      <form onSubmit={handleSubmit}>
          {/* ... (reszta formularza) */}
          {error && <div className="error">{error}</div>}
      </form>
  );
}

// ... (reszta kodu)


// Komponent Gallery
function Gallery() {
  const images = [
    process.env.PUBLIC_URL + '/images/0_1.png',
    process.env.PUBLIC_URL + '/images/0_2.png',
    // ... (dodaj więcej ścieżek do zdjęć, jeśli są dostępne)
  ];

  return (
    <div className="gallery">
      {images.map((image, index) => (
        <img key={index} src={image} alt={`Example ${index + 1}`} />
      ))}
    </div>
  );
}

// Główny komponent App
function App() {
  return (
    <div className="App">
      <header className="App-header">
        {/* Logo, nazwa warsztatu, dane kontaktowe */}
      </header>
      <section className="App-services">
        {/* Opis usług */}
      </section>
      <ContactForm />
      <Gallery />
      <footer className="App-footer">
        {/* Informacje prawne, media społecznościowe, mapa */}
      </footer>
    </div>
  );
}

// Eksportowanie głównego komponentu App, aby mógł być używany w innych częściach projektu
export default App;
